import React, { useState } from "react";
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  runTransaction,
  Timestamp
} from "firebase/firestore";
import { db, auth } from "../firebase";

const fixedFieldsInitial = {
  date: "",
  months: "",
  indentNo: "",
  origin: "",
  destination: "",
  customer: "",
  customerType: "",
  vehicleNo: "",
  vendor: "",
  salesRate: "",
  buyRate: ""
};

const customerFields = {
  advanceToBePaid: "",
  advanceReceived: "",
  advDeviation: "",
  advanceRecDate: "",
  validatedAdvanceUTR: "",
  validatedAdvanceAmount: "",
  balance: "",
  processingCharges: "",
  inwardMisCharges: "",
  outwardMisCharges: "",
  balanceReceived: "",
  remainingBalance: "",
  balanceRecDate: "",
  validatedBalanceUTR: "",
  validatedBalanceUTRAmount: ""
};

const vendorFields = {
  vendorOutwardPayment: "",
  paidAmount: "",
  balancePending: "",
  vendorRemark: ""
};

const podFields = {
  podVendorDate: "",
  podSendToCustomerDate: "",
  docNo: "",
  podCustomerRec: "",
  today: "",
  balanceOverdueDays: "",
  toBeCollectedAmount: ""
};

const ManualEntryForm = ({ onAddRow }) => {
  const [fixedFields, setFixedFields] = useState(fixedFieldsInitial);
  const [customerData, setCustomerData] = useState(customerFields);
  const [vendorData, setVendorData] = useState(vendorFields);
  const [podData, setPodData] = useState(podFields);
  const [fleetNumber, setFleetNumber] = useState(null);
  const [docId, setDocId] = useState(null);

  const [showCustomer, setShowCustomer] = useState(false);
  const [showVendor, setShowVendor] = useState(false);
  const [showPod, setShowPod] = useState(false);

  const handleFixedChange = (e) => {
    const { name, value } = e.target;
    setFixedFields({ ...fixedFields, [name]: value });
  };

  const handleSectionChange = (e, section) => {
    const { name, value } = e.target;
    if (section === "customer") setCustomerData({ ...customerData, [name]: value });
    if (section === "vendor") setVendorData({ ...vendorData, [name]: value });
    if (section === "pod") setPodData({ ...podData, [name]: value });
  };

  const getNextFleetNumber = async () => {
    const counterRef = doc(db, "Counters", "fleet_counter");
    const newFleetNo = await runTransaction(db, async (transaction) => {
      const docSnap = await transaction.get(counterRef);
      if (!docSnap.exists()) throw new Error("Counter doc missing");
      const current = docSnap.data().nextFleetNo || 1;
      transaction.update(counterRef, { nextFleetNo: current + 1 });
      return current;
    });
    return newFleetNo;
  };

  const convertDateFields = (data) => {
    const result = {};
    Object.entries(data).forEach(([key, value]) => {
      if (key.toLowerCase().includes("date") && value) {
        const parsed = new Date(value);
        result[key] = isNaN(parsed) ? value : Timestamp.fromDate(parsed);
      } else {
        result[key] = value;
      }
    });
    return result;
  };

  const handleFixedSave = async () => {
    try {
      const user = auth.currentUser;
      const fleetNo = await getNextFleetNumber();
      const enrichedData = {
        fleetNumber: fleetNo,
        ...convertDateFields(fixedFields),
        createdAt: new Date(),
        createdBy: user?.email || "anonymous",
        isCurrent: true,
        versionDate: new Date(),
        updateDescription: ""
      };

      const docRef = await addDoc(collection(db, "fleet_records"), enrichedData);
      setFleetNumber(fleetNo);
      setDocId(docRef.id);
      localStorage.setItem("inProgressFleetNumber", fleetNo);
      await navigator.clipboard.writeText(String(fleetNo));
      alert(`✅ Fixed fields saved. Fleet Number copied: ${fleetNo}`);
    } catch (err) {
      alert("❌ Error saving fixed fields: " + err.message);
    }
  };

  const saveSection = async (sectionName, sectionData) => {
    if (!docId) {
      alert("⚠️ Fixed fields must be saved first.");
      return;
    }
    try {
      const docRef = doc(db, "fleet_records", docId);
      await updateDoc(docRef, {
        [`${sectionName}`]: convertDateFields(sectionData)
      });
      alert(`✅ ${sectionName} section saved`);
    } catch (err) {
      alert("❌ Error saving section: " + err.message);
    }
  };

  return (
    <div style={{ marginTop: 20 }}>
      <h4>Step 1: Fixed Fields</h4>
      {Object.keys(fixedFieldsInitial).map((key) => {
        const isDate = key.toLowerCase().includes("date");
        const label = isDate ? `${key} (dd/mm/yyyy)` : key;
        return (
          <input
            key={key}
            name={key}
            placeholder={label}
            value={fixedFields[key]}
            onChange={handleFixedChange}
            disabled={!!fleetNumber}
            style={{ margin: 4, width: "220px" }}
          />
        );
      })}
      {!fleetNumber && (
        <div>
          <button onClick={handleFixedSave}>💾 Save & Continue</button>
        </div>
      )}
      {fleetNumber && (
        <>
          <p style={{ color: "green" }}>✅ Saved. Fleet No: {fleetNumber}</p>

          <h4>Step 2: Select Master Sections</h4>
          <label>
            <input type="checkbox" checked={showCustomer} onChange={() => setShowCustomer(!showCustomer)} /> Customer Master
          </label>
          <label style={{ marginLeft: 15 }}>
            <input type="checkbox" checked={showVendor} onChange={() => setShowVendor(!showVendor)} /> Vendor Master
          </label>
          <label style={{ marginLeft: 15 }}>
            <input type="checkbox" checked={showPod} onChange={() => setShowPod(!showPod)} /> POD Master
          </label>

          {showCustomer && (
            <>
              <h5>Customer Master</h5>
              {Object.keys(customerFields).map((key) => {
                const isDate = key.toLowerCase().includes("date");
                const label = isDate ? `${key} (dd/mm/yyyy)` : key;
                return (
                  <input
                    key={key}
                    name={key}
                    placeholder={label}
                    value={customerData[key]}
                    onChange={(e) => handleSectionChange(e, "customer")}
                    style={{ margin: 4, width: "220px" }}
                  />
                );
              })}
              <button onClick={() => saveSection("customerMaster", customerData)}>Save Customer Section</button>
            </>
          )}

          {showVendor && (
            <>
              <h5>Vendor Master</h5>
              {Object.keys(vendorFields).map((key) => (
                <input
                  key={key}
                  name={key}
                  placeholder={key}
                  value={vendorData[key]}
                  onChange={(e) => handleSectionChange(e, "vendor")}
                  style={{ margin: 4, width: "220px" }}
                />
              ))}
              <button onClick={() => saveSection("vendorMaster", vendorData)}>Save Vendor Section</button>
            </>
          )}

          {showPod && (
            <>
              <h5>POD Master</h5>
              {Object.keys(podFields).map((key) => {
                const isDate = key.toLowerCase().includes("date");
                const label = isDate ? `${key} (dd/mm/yyyy)` : key;
                return (
                  <input
                    key={key}
                    name={key}
                    placeholder={label}
                    value={podData[key]}
                    onChange={(e) => handleSectionChange(e, "pod")}
                    style={{ margin: 4, width: "220px" }}
                  />
                );
              })}
              <button onClick={() => saveSection("podMaster", podData)}>Save POD Section</button>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default ManualEntryForm;
