// UploadForm.js
import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import { db, auth } from "../firebase";
import {
  collection,
  addDoc,
  doc,
  runTransaction,
  Timestamp,
} from "firebase/firestore";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// Final field structure
const finalFields = [
  // Fixed
  "date", "deliveryDate", "months", "origin", "destination", "customer", "customerType", "vehicleNo", "vendor", "salesRate", "buyRate",
  // Customer
  "toBeAdvance", "advanceReceived", "advDeviation", "advanceRecDate", "validatedAdvanceUTRDescription", "validatedAdvanceAmount", "balance", "processingCharges", "inwardMisCharges", "outwardMisCharges", "balanceReceived", "remainingBalance", "balanceRecDate", "validatedBalanceUTR", "validatedBalanceUTRAmount",
  // Vendor
  "vendorOutwardPayment", "paidAmount", "balancePending", "vendorRemark",
  // POD
  "podVendorDate", "podSendToCustomerDate", "docNo", "podCustomerRec", "today", "balanceOverdueDays", "toBeCollectedAmount"
];

const dateFields = [
  "date", "deliveryDate", "advanceRecDate", "balanceRecDate",
  "podVendorDate", "podSendToCustomerDate", "podCustomerRec", "today"
];

// Label ‚Üî Key mapping
const keyToLabel = {
  // Fixed
  date: "Placement Date",
  deliveryDate: "Delivery Date",
  months: "Months",
  origin: "Origin",
  destination: "Destination",
  customer: "Customer",
  customerType: "Customer Type",
  vehicleNo: "Vehicle No",
  vendor: "Vendor",
  salesRate: "Sales Rate",
  buyRate: "Buy Rate",

  // Customer Master
  toBeAdvance: "To Be Advance(Sales)",
  advanceReceived: "Advance/Payment Received",
  advDeviation: "Adv Deviation(as on Date)",
  advanceRecDate: "Advance Rec Date",
  validatedAdvanceUTRDescription: "Validated-Advance UTR Discription",
  validatedAdvanceAmount: "Validated UTR -Advance Amount",
  balance: "Balance",
  processingCharges: "Processing charges",
  inwardMisCharges: "Inward-Mis Charges",
  outwardMisCharges: "Outward-Mis Charges",
  balanceReceived: "Bal Receved",
  remainingBalance: "Remaining Balance",
  balanceRecDate: "Balance Rec Date",
  validatedBalanceUTR: "Validated-Balance UTR",
  validatedBalanceUTRAmount: "Validate Balance UTR-Amount",

  // Vendor Master
  vendorOutwardPayment: "Vendor Outward Payment",
  paidAmount: "Paid Amount",
  balancePending: "Balance Pending",
  vendorRemark: "Vendor Remark",

  // POD Master
  podVendorDate: "POD Rev-Vendor-Date",
  podSendToCustomerDate: "POD-Send to Customer Date",
  docNo: "Doc No",
  podCustomerRec: "POD-Customer Rec",
  today: "Today",
  balanceOverdueDays: "Balance Overdue Days",
  toBeCollectedAmount: "To Be Collected Amount"
};

const labelToKey = Object.fromEntries(Object.entries(keyToLabel).map(([k, v]) => [v, k]));

const cleanDate = (value) => {
  const parsed = new Date(value);
  return !isNaN(parsed) ? parsed.toISOString().split("T")[0] : "";
};

const UploadForm = () => {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    const saved = localStorage.getItem("uploadPreviewRows");
    if (saved) setRows(JSON.parse(saved));
  }, []);

  const handleFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.name.endsWith('.xls') && !file.name.endsWith('.xlsx')) {
      toast.error("‚ùå Please upload a valid Excel file");
      return;
    }

    const reader = new FileReader();
    reader.onload = (event) => {
      const wb = XLSX.read(event.target.result, { type: "binary" });
      const sheet = wb.Sheets[wb.SheetNames[0]];
      const raw = XLSX.utils.sheet_to_json(sheet, { defval: "" });

      const mapped = raw.map((row) => {
        const obj = {};
        finalFields.forEach((key) => {
          const label = keyToLabel[key];
          let val = row[label] || "";
          if (dateFields.includes(key)) val = cleanDate(val);
          obj[key] = val;
        });
        return obj;
      });

      setRows(mapped);
      localStorage.setItem("uploadPreviewRows", JSON.stringify(mapped));
      toast.success("üìÅ File uploaded & ready to review");
    };

    reader.readAsBinaryString(file);
  };

  const getNextIndentNumber = async () => {
    const counterRef = doc(db, "Counters", "fleet_counter");
    return await runTransaction(db, async (tx) => {
      const snap = await tx.get(counterRef);
      if (!snap.exists()) throw new Error("Counter not found");
      const current = snap.data().nextFleetNo || 1;
      tx.update(counterRef, { nextFleetNo: current + 1 });
      return current;
    });
  };

  const convertDateFields = (row) => {
    const out = {};
    for (const [key, val] of Object.entries(row)) {
      if (dateFields.includes(key)) {
        const parsed = new Date(val);
        out[key] = isNaN(parsed) ? val : Timestamp.fromDate(parsed);
      } else {
        out[key] = val;
      }
    }
    return out;
  };

  const handleSubmitAll = async () => {
    if (!rows.length) {
      toast.warning("‚ö†Ô∏è No rows to submit");
      return;
    }

    try {
      for (const row of rows) {
        const indentNo = await getNextIndentNumber();
        const payload = {
          indentNumber: indentNo,
          ...convertDateFields(row),
          isCurrent: true,
          versionDate: new Date(),
          updateDescription: "Excel Upload",
          createdBy: auth.currentUser?.email || "anonymous",
          createdAt: new Date()
        };
        await addDoc(collection(db, "fleet_records"), payload);
      }

      toast.success(`‚úÖ Uploaded ${rows.length} records`);
      setRows([]);
      localStorage.removeItem("uploadPreviewRows");
    } catch (err) {
      console.error(err);
      toast.error("‚ùå Upload failed: " + err.message);
    }
  };

  const downloadTemplate = () => {
    const sampleRow = {};
    finalFields.forEach((key) => {
      sampleRow[keyToLabel[key]] = dateFields.includes(key) ? "2025-07-01" : "Sample";
    });

    const ws = XLSX.utils.json_to_sheet([sampleRow]);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Template");
    XLSX.writeFile(wb, "FleetUploadTemplate.xlsx");
  };

  return (
    <div className="container mt-4">
      <h4>üì§ Upload Excel File</h4>
      <div style={{ display: "flex", gap: "15px", marginTop: "10px" }}>
        <input type="file" accept=".xls,.xlsx" onChange={handleFile} />
        <button className="btn btn-secondary" onClick={downloadTemplate}>
          ‚¨áÔ∏è Download Template
        </button>
      </div>

      {rows.length > 0 && (
        <div style={{ marginTop: "25px" }}>
          <h5>üìÑ Preview & Edit</h5>
          <div className="table-scroll-x">
            <table>
              <thead>
                <tr>
                  {finalFields.map((key) => (
                    <th key={key}>{keyToLabel[key]}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {rows.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {finalFields.map((key) => (
                      <td key={key}>
                        <input
                          type={dateFields.includes(key) ? "date" : "text"}
                          value={row[key]}
                          onChange={(e) => {
                            const updated = [...rows];
                            updated[rowIndex][key] = e.target.value;
                            setRows(updated);
                            localStorage.setItem("uploadPreviewRows", JSON.stringify(updated));
                          }}
                        />
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div style={{ textAlign: "right", marginTop: "15px" }}>
            <button onClick={handleSubmitAll} className="btn btn-success me-3">
              ‚úÖ Submit All Records
            </button>
            <button
              onClick={() => {
                setRows([]);
                localStorage.removeItem("uploadPreviewRows");
                toast.info("üßπ Cleared preview data");
              }}
              className="btn btn-warning"
            >
              üîÑ Upload New Sheet
            </button>
          </div>
        </div>
      )}
    </div>
  );
};
// UploadForm.js (add this at the end)
export const handleExcelUpload = (file, setRowsCallback) => {
  const reader = new FileReader();
  reader.onload = (event) => {
    const workbook = XLSX.read(event.target.result, { type: "binary" });
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const raw = XLSX.utils.sheet_to_json(sheet, { defval: "" });

    const mapped = raw.map((row) => {
      const result = {};
      finalFields.forEach((key) => {
        const label = keyToLabel[key];
        let val = row[label] || "";
        if (dateFields.includes(key)) val = cleanDate(val);
        result[key] = val;
      });
      return result;
    });

    localStorage.setItem("uploadPreviewRows", JSON.stringify(mapped));
    toast.success("‚úÖ Excel data ready. Scroll to preview & submit.");
    setRowsCallback(mapped);
  };
  reader.readAsBinaryString(file);
};

export default UploadForm;
