  "Vehicle type": "VehicleType",
  "Driver No": "DriverNo",
  "Dispatch Date": "DispatchDate",
  "Deliver Date": "DeliverDate",
  "Offloading Date": "OffloadingDate",
  "E-way Bill": "EwayBill",
  "LR No.": "LRNo",
  "Soft Copy POD Rec": "SoftCopyPODRec",
  "Hard Copy POD Rec": "HardCopyPODRec",
  "Customer -Sale rate": "CustomerSaleRate",
  "Advance to be Paid": "AdvanceToBePaid",
  "Advance Rec": "AdvanceRec",
  "Advance UTR": "AdvanceUTR",
  "Advance Rec-Date": "AdvanceRecDate",
  "Balance Pending": "BalancePending",
  "Detention Charges": "DetentionCharges",
  "Loading/Unloading Charges": "LoadingUnloadingCharges",
  "Miscellaneous Charges.": "MiscCharges",
  "Processing Charges": "ProcessingCharges",
  "Net Balance": "NetBalance",
  "Balance Rec Amount": "BalanceRecAmount",
  "Balance UTR": "BalanceUTR",
  "Balance Rec Date": "BalanceRecDate",
  "Remaining Balance": "RemainingBalance",
  "Remaining Balance UTR": "RemainingBalanceUTR",
  "Remaining Balance Date": "RemainingBalanceDate",
  "Supplier Buy Rate": "SupplierBuyRate",
  "Supplier Advance Pay": "SupplierAdvancePay",
  "Supplier Advance Paid": "SupplierAdvancePaid",
  "Supplier Mis Charges": "SupplierMisCharges",
  "Supplier Invoice No.": "SupplierInvoiceNo",
  "Supplier Advance UTR": "SupplierAdvanceUTR",
  "Supplier Advance Pay-Date": "SupplierAdvancePayDate",
  "Supplier Balance Pending": "SupplierBalancePending",
  "Supplier Balance Paid Amount": "SupplierBalancePaidAmount",
  "Supplier Balance Paid UTR": "SupplierBalancePaidUTR",
  "Supplier Balance Paid Date": "SupplierBalancePaidDate",
  "Remaining Supplier Amount": "RemainingSupplierAmount",
  "POD Rec Date": "PODRecDate",
  "POD Send to Customer Date": "PODSendToCustomerDate",
  "POD Docket No.": "PODDocketNo",
  "POD Rec By Customer": "PODRecByCustomer",
  "POD Deduction If any": "PODDeductionIfAny",
  "Gross Profit": "GrossProfit",
  "Bad Debts": "BadDebts",
  "Net Profit": "NetProfit",
  "UniqueNo": "UniqueNo",
  "isCurrent": "isCurrent",
  "createdBy": "createdBy",
  "createdAt": "createdAt"
};

const keyToLabel = Object.fromEntries(Object.entries(labelToKey).map(([k, v]) => [v, k]));


const testConnection = async () => {
  try {
    const docRef = await addDoc(collection(db, "test_connection"), {
      message: "Testing Firestore connection",
      timestamp: new Date()
    });
    console.log("✅ Firestore write succeeded with ID:", docRef.id);
    alert("✅ Firebase is connected. Test document written!");
  } catch (error) {
    console.error("❌ Firebase connection failed:", error);
    alert("❌ Firebase connection failed: " + error.message);
  }
};

function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);
  const [history, setHistory] = useState([]);
  const [searchKey, setSearchKey] = useState("");
  const [searchField, setSearchField] = useState("UniqueNo");

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      console.log("🔥 Auth State Changed. User:", currentUser);
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  const handleSave = async () => {
    try {
      const batch = records.map(record =>
        addDoc(collection(db, "fleet_records"), {
          ...record,
          createdBy: user.email,
          createdAt: new Date(),
          isCurrent: true,
          versionDate: new Date()
        })
      );
      await Promise.all(batch);
      alert("Records saved to Firestore!");
    } catch (error) {
      alert("Error saving records: " + error.message);
    }
  };

  const handleSearch = async () => {
    try {
      // Get all versions (full history)
      const hq = query(collection(db, "fleet_records"), where(searchField, "==", searchKey));
      const hSnapshot = await getDocs(hq);
      const allVersions = [];
      const currentOnly = [];

      hSnapshot.forEach((docSnap) => {
        const row = { id: docSnap.id, ...docSnap.data() };

        // ✅ Treat missing isCurrent as true (for old records)
        if (row.isCurrent === undefined) {
          row.isCurrent = true;
        }

        allVersions.push(row);
        if (row.isCurrent === true) currentOnly.push(row);
      });

      setRecords(currentOnly);
      setHistory(allVersions);
    } catch (error) {
      alert("Search failed: " + error.message);
    }
  };

  const handleUpdate = async (row) => {
    try {
      const { id, ...rest } = row;
      const cleanData = {};
      for (const key in rest) {
        cleanData[key] =
          typeof rest[key] === "string" || typeof rest[key] === "number"
            ? rest[key]
            : String(rest[key] ?? "");
      }

      await updateDoc(doc(db, "fleet_records", id), {
        isCurrent: false,
        expiredAt: new Date(),
        modifiedBy: user.email
      });

      const newVersion = {
        ...cleanData,
        createdAt: new Date(),
        createdBy: user.email,
        isCurrent: true,
        versionDate: new Date()
      };

      const docRef = await addDoc(collection(db, "fleet_records"), newVersion);

      const updatedRecords = records.map((r) =>
        r.id === id ? { ...newVersion, id: docRef.id } : r
      );
      setRecords(updatedRecords);
      handleSearch();

      alert("🆕 SCD Type 2 update saved successfully.");
    } catch (error) {
      alert("❌ Update failed: " + error.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteDoc(doc(db, "fleet_records", id));
      setRecords(records.filter((r) => r.id !== id));
      alert("🗑️ Record deleted successfully.");
    } catch (error) {
      alert("❌ Delete failed: " + error.message);
    }
  };

  if (!user) return <Auth />;

  return (
    <div style={{ padding: 20 }}>
      <h2>Welcome, {user.email}</h2>
      <button onClick={() => signOut(auth)}>Logout</button>
      <br />
      <button onClick={testConnection}>Test Firebase Connection</button>
      <hr />

      <UploadForm onDataParsed={(data) => setRecords([...records, ...data])} />
      <ManualEntryForm onAddRow={(row) => setRecords([...records, row])} />

      <hr />
      <h4>Search Existing Records</h4>
      <select value={searchField} onChange={(e) => setSearchField(e.target.value)}>
        <option value="UniqueNo">UniqueNo</option>
        <option value="Broker">Broker</option>
      </select>
      <input
        type="text"
        placeholder="Search value"
        value={searchKey}
        onChange={(e) => setSearchKey(e.target.value)}
        style={{ margin: "0 10px" }}
      />
      <button onClick={handleSearch}>Search</button>

      <hr />
      <h4>Editable Current Records</h4>
<div className="table-scroll-x">
  <table>
    <thead>
      <tr>
        {[
          "UniqueNo", "Indent No", "Indent Date", "Placement Date", "Customer", "Customer Type",
          "Customer Billing Type", "Sourcing (Vendor)", "Vendor Type", "Vendor Billing Type",
          "Origin", "Destination", "Vehicle No", "Vehicle type", "Driver No", "Dispatch Date",
          "Deliver Date", "Offloading Date", "E-way Bill", "LR No.", "Soft Copy POD Rec",
          "Hard Copy POD Rec", "Customer -Sale rate", "Advance to be Paid", "Advance Rec",
          "Advance UTR", "Advance Rec-Date", "Balance Pending", "Detention Charges",
          "Loading/Unloading Charges", "Miscellaneous Charges.", "Processing Charges",
          "Net Balance", "Balance Rec Amount", "Balance UTR", "Balance Rec Date",
          "Remaining Balance", "Remaining Balance UTR", "Remaining Balance Date",
          "Supplier Buy Rate", "Supplier Advance Pay", "Supplier Advance Paid",
          "Supplier Mis Charges", "Supplier Invoice No.", "Supplier Advance UTR",
          "Supplier Advance Pay-Date", "Supplier Balance Pending", "Supplier Balance Paid Amount",
          "Supplier Balance Paid UTR", "Supplier Balance Paid Date", "Remaining Supplier Amount",
          "POD Rec Date", "POD Send to Customer Date", "POD Docket No.", "POD Rec By Customer",
          "POD Deduction If any", "Gross Profit", "Bad Debts", "Net Profit",
          "isCurrent", "createdBy", "createdAt"
        ].map((col) => (
          <th key={col}>{col}</th>
        ))}
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
  {records.map((row, rowIndex) => {
    if (!row.UniqueNo) {
      row.UniqueNo = `UNQ-${Date.now()}-${Math.floor(Math.random() * 1000)}`;
    }

    const readOnlyFields = ["UniqueNo", "createdAt", "createdBy", "isCurrent"];

    return (
      <tr key={rowIndex}>
        {Object.entries(labelToKey).map(([label, key], colIndex) => (
          <td key={colIndex}>
            <input
              type="text"
              value={
                typeof row[key] === "object" && row[key]?.seconds
                  ? new Date(row[key].seconds * 1000).toLocaleString()
                  : String(row[key] ?? "")
              }
              onChange={(e) => {
                const updated = [...records];
                updated[rowIndex][key] = e.target.value;
                setRecords(updated);
              }}
              style={{ width: "120px" }}
              readOnly={readOnlyFields.includes(key)}
            />
          </td>
        ))}
        <td>
          <button onClick={() => handleUpdate(row)}>Save</button>
          <button
            onClick={() => {
              if (window.confirm("Are you sure you want to delete this record?")) {
                handleDelete(row.id);
              }
            }}
            style={{ marginLeft: "8px", color: "red" }}
          >
            Delete
          </button>
        </td>
      </tr>
    );
  })}
</tbody>


  </table>
</div>



      {history.length > 0 && (
  <>
    <hr />
    <h4>🔍 Full Version History</h4>

    {/* ✅ Dynamically build all unique columns across all rows */}
    {(() => {
      const allKeys = new Set();
      history.forEach(row => Object.keys(row).forEach(k => allKeys.add(k)));
      const columnList = Array.from(allKeys);

      return (
        <div className="table-scroll-x">
  <table>
    <thead>
      <tr>
        {columnList.map((col) => (
          <th key={col}>{col}</th>
        ))}
      </tr>
    </thead>
    <tbody>
      {[...history]
        .sort((a, b) => {
          if (a.isCurrent) return -1;
          if (b.isCurrent) return 1;
          const aTime = a.expiredAt?.seconds || 0;
          const bTime = b.expiredAt?.seconds || 0;
          return bTime - aTime;
        })
        .map((row, i) => (
          <tr key={i}>
            {columnList.map((col, j) => (
              <td key={j}>
                {typeof row[col] === 'object' && row[col]?.seconds
                  ? new Date(row[col].seconds * 1000).toLocaleString()
                  : String(row[col] ?? "")}
              </td>
            ))}
          </tr>
        ))}
    </tbody>
  </table>
</div>
      );
    })()}
  </>
)}

    </div>
  );
}

export default App;
