// UploadForm.js
import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import { db, auth } from "../firebase";
import {
  collection,
  addDoc,
  doc,
  runTransaction,
  Timestamp,
} from "firebase/firestore";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// 1. Final field structure (flattened)
const finalFields = [
  // Fixed
  "date", "deliveryDate", "months", "origin", "destination", "customer", "customerType", "vehicleNo", "vendor", "salesRate", "buyRate",
  // Customer
  "toBeAdvance", "advanceReceived", "advDeviation", "advanceRecDate", "validatedAdvanceUTRDescription", "validatedAdvanceAmount", "balance", "processingCharges", "inwardMisCharges", "outwardMisCharges", "balanceReceived", "remainingBalance", "balanceRecDate", "validatedBalanceUTR", "validatedBalanceUTRAmount",
  // Vendor
  "vendorOutwardPayment", "paidAmount", "balancePending", "vendorRemark",
  // POD
  "podVendorDate", "podSendToCustomerDate", "docNo", "podCustomerRec", "today", "balanceOverdueDays", "toBeCollectedAmount"
];

const dateFields = [
  "date", "deliveryDate",
  "advanceRecDate", "balanceRecDate",
  "podVendorDate", "podSendToCustomerDate", "podCustomerRec", "today"
];

// Mapping for human-readable labels
const keyToLabel = finalFields.reduce((acc, key) => {
  const label = key
    .replace(/([a-z])([A-Z])/g, "$1 $2")
    .replace(/_/g, " ")
    .replace(/^./, (s) => s.toUpperCase());
  acc[key] = label;
  return acc;
}, {});

// Reverse mapping for parsing uploaded Excel
const labelToKey = Object.entries(keyToLabel).reduce((acc, [key, label]) => {
  acc[label] = key;
  return acc;
}, {});

const cleanDate = (value) => {
  const parsed = new Date(value);
  return !isNaN(parsed) ? parsed.toISOString().split("T")[0] : "";
};

const UploadForm = () => {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    const saved = localStorage.getItem("uploadPreviewRows");
    if (saved) {
      setRows(JSON.parse(saved));
    }
  }, []);

  const handleFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.name.endsWith('.xls') && !file.name.endsWith('.xlsx')) {
      toast.error("‚ùå Please upload a valid Excel file (.xls or .xlsx)");
      return;
    }

    const reader = new FileReader();
    reader.onload = (event) => {
      const workbook = XLSX.read(event.target.result, { type: "binary" });
      const sheet = workbook.Sheets[workbook.SheetNames[0]];
      const raw = XLSX.utils.sheet_to_json(sheet, { defval: "" });

      const mapped = raw.map((row) => {
        const result = {};
        finalFields.forEach((key) => {
          const label = keyToLabel[key];
          let val = row[label] || "";
          if (dateFields.includes(key)) val = cleanDate(val);
          result[key] = val;
        });
        return result;
      });

      setRows(mapped);
      localStorage.setItem("uploadPreviewRows", JSON.stringify(mapped));
    };

    reader.readAsBinaryString(file);
  };

  const handleSubmitAll = async () => {
    if (!rows.length) {
      toast.warn("‚ö†Ô∏è No data to submit");
      return;
    }

    try {
      const batch = [];
      for (const row of rows) {
        const indentNo = await getNextIndentNumber();
        const enriched = {
          indentNumber: indentNo,
          ...convertDateFields(row),
          createdAt: new Date(),
          createdBy: auth.currentUser?.email || "anonymous",
          isCurrent: true,
          versionDate: new Date(),
          updateDescription: "Excel Upload"
        };
        await addDoc(collection(db, "fleet_records"), enriched);
        batch.push(indentNo);
      }

      toast.success(`‚úÖ Uploaded ${batch.length} records`);
      localStorage.removeItem("uploadPreviewRows");
      setRows([]);
    } catch (err) {
      console.error(err);
      toast.error("‚ùå Upload failed: " + err.message);
    }
  };

  const getNextIndentNumber = async () => {
    const counterRef = doc(db, "Counters", "fleet_counter");
    return await runTransaction(db, async (transaction) => {
      const snap = await transaction.get(counterRef);
      if (!snap.exists()) throw new Error("Counter doc missing");
      const current = snap.data().nextFleetNo || 1;
      transaction.update(counterRef, { nextFleetNo: current + 1 });
      return current;
    });
  };

  const convertDateFields = (data) => {
    const result = {};
    Object.entries(data).forEach(([key, val]) => {
      if (dateFields.includes(key)) {
        const parsed = new Date(val);
        result[key] = isNaN(parsed) ? val : Timestamp.fromDate(parsed);
      } else {
        result[key] = val;
      }
    });
    return result;
  };

  const downloadTemplate = () => {
  const sampleRow = {
    // Fixed
    "Placement Date": "2025-07-01",
    "Delivery Date": "2025-07-02",
    "Months": "July",
    "Origin": "Delhi",
    "Destination": "Mumbai",
    "Customer": "ABC Ltd.",
    "Customer Type": "Regular",
    "Vehicle No": "MH12AB1234",
    "Vendor": "XYZ Logistics",
    "Sales Rate": "1000",
    "Buy Rate": "800",

    // Customer Master
    "To Be Advance(Sales)": "",
    "Advance/Payment Received": "",
    "Adv Deviation(as on Date)": "",
    "Advance Rec Date": "2025-07-01",
    "Validated-Advance UTR Discription": "",
    "Validated UTR -Advance Amount": "",
    "Balance": "",
    "Processing charges": "",
    "Inward-Mis Charges": "",
    "Outward-Mis Charges": "",
    "Bal Receved": "",
    "Remaining Balance": "",
    "Balance Rec Date": "2025-07-01",
    "Validated-Balance UTR": "",
    "Validate Balance UTR-Amount": "",

    // Vendor Master
    "Vendor Outward Payment": "",
    "Paid Amount": "",
    "Balance Pending": "",
    "Vendor Remark": "",

    // POD Master
    "POD Rev-Vendor-Date": "2025-07-01",
    "POD-Send to Customer Date": "2025-07-01",
    "Doc No": "",
    "POD-Customer Rec": "2025-07-01",
    "Today": "2025-07-01",
    "Balance Overdue Days": "",
    "To Be Collected Amount": ""
  };

  const sheet = XLSX.utils.json_to_sheet([sampleRow]);
  const wb = XLSX.utils.book_new();
  XLSX.utils.book_append_sheet(wb, sheet, "Template");
  XLSX.writeFile(wb, "FleetUploadTemplate.xlsx");
};


  return (
    <div className="container mt-4">
      <h4>üì§ Upload Excel File</h4>
      <div style={{ display: "flex", gap: "15px", marginTop: "10px" }}>
        <input type="file" accept=".xlsx, .xls" onChange={handleFile} />
        <button className="btn btn-secondary" onClick={downloadTemplate}>
          ‚¨áÔ∏è Download Template
        </button>
      </div>

      <div style={{ marginTop: "20px" }}>
        {rows.length > 0 && (
          <>
            <h5>üìÑ Preview & Edit Data</h5>
            <div className="table-scroll-x">
              <table>
                <thead>
                  <tr>
                    {finalFields.map((key) => (
                      <th key={key}>{keyToLabel[key]}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {rows.map((row, rowIndex) => (
                    <tr key={rowIndex}>
                      {finalFields.map((key) => (
                        <td key={key}>
                          <input
                            type={dateFields.includes(key) ? "date" : "text"}
                            value={row[key]}
                            onChange={(e) => {
                              const updated = [...rows];
                              updated[rowIndex][key] = e.target.value;
                              setRows(updated);
                              localStorage.setItem("uploadPreviewRows", JSON.stringify(updated));
                            }}
                          />
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div style={{ textAlign: "right", marginTop: "20px" }}>
              <button onClick={handleSubmitAll} className="btn btn-success me-3">
                ‚úÖ Submit All Records
              </button>
              <button
                onClick={() => {
                  setRows([]);
                  localStorage.removeItem("uploadPreviewRows");
                  toast.info("üßπ Cleared uploaded preview data");
                }}
                className="btn btn-warning"
              >
                üîÑ Upload New Sheet
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default UploadForm;
