// UploadForm.js
// UploadForm.js
import React, { useState } from "react";
import { addDoc, collection, doc, runTransaction } from "firebase/firestore";
import * as XLSX from "xlsx";
import { db, auth } from "../firebase";

const UploadForm = () => {
  const [previewData, setPreviewData] = useState([]);
  const [statusMap, setStatusMap] = useState({});
  const [fleetNumbers, setFleetNumbers] = useState({});

  const parseExcel = (file) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const workbook = XLSX.read(e.target.result, { type: "binary" });
      const sheet = workbook.Sheets[workbook.SheetNames[0]];
      const json = XLSX.utils.sheet_to_json(sheet, { header: 1 });
      const headers = json[1];
      const rows = json.slice(2).map((row) => {
        const obj = {};
        headers.forEach((h, j) => {
          obj[h] = row[j] ?? "";
        });
        return obj;
      });
      setPreviewData(rows);
      setStatusMap({});
      setFleetNumbers({});
    };
    reader.readAsBinaryString(file);
  };

  const getNextFleetNumber = async () => {
    const counterRef = doc(db, "Counters", "fleet_counter");
    const newFleetNo = await runTransaction(db, async (transaction) => {
      const docSnap = await transaction.get(counterRef);
      if (!docSnap.exists()) throw new Error("Counter doc missing");
      const current = docSnap.data().nextFleetNo || 1;
      transaction.update(counterRef, { nextFleetNo: current + 1 });
      return current;
    });
    return newFleetNo;
  };

  const saveRow = async (row, i) => {
    const user = auth.currentUser;
    try {
      const newFleetNo = await getNextFleetNumber();
      const enriched = {
        ...row,
        fleetNumber: newFleetNo,
        createdAt: new Date(),
        createdBy: user?.email || "anonymous",
        isCurrent: true,
        versionDate: new Date()
      };
      await addDoc(collection(db, "fleet_records"), enriched);
      setStatusMap((prev) => ({ ...prev, [i]: "✅ Saved" }));
      setFleetNumbers((prev) => ({ ...prev, [i]: newFleetNo }));
    } catch (err) {
      setStatusMap((prev) => ({ ...prev, [i]: "❌ Failed" }));
    }
  };

  const saveAll = async () => {
    for (let i = 0; i < previewData.length; i++) {
      if (!statusMap[i]) await saveRow(previewData[i], i);
    }
  };

  const handleEdit = (i, key, value) => {
    const updated = [...previewData];
    updated[i][key] = value;
    setPreviewData(updated);
  };

  const deleteRow = (i) => {
    setPreviewData(previewData.filter((_, idx) => idx !== i));
    const updatedStatus = { ...statusMap };
    delete updatedStatus[i];
    setStatusMap(updatedStatus);
    const updatedFleet = { ...fleetNumbers };
    delete updatedFleet[i];
    setFleetNumbers(updatedFleet);
  };

  const resetTable = () => {
    setPreviewData([]);
    setStatusMap({});
    setFleetNumbers({});
  };

  return (
    <div style={{ marginBottom: 40 }}>
      <h4>Upload Excel & Preview</h4>
      <input
        type="file"
        accept=".xlsx, .xls"
        onChange={(e) => parseExcel(e.target.files[0])}
      />
      {previewData.length > 0 && (
        <>
          <h5 style={{ marginTop: 20 }}>📄 Preview Table (Excel Upload)</h5>
          <button onClick={saveAll}>💾 Save All</button>
          <button onClick={resetTable} style={{ marginLeft: 10, color: "red" }}>Reset</button>
          <div className="table-scroll-x">
            <table>
              <thead>
                <tr>
                  <th>Fleet Number</th>
                  {Object.keys(previewData[0]).map((key) => (
                    <th key={key}>{key}</th>
                  ))}
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {previewData.map((row, i) => (
                  <tr key={i}>
                    <td>{fleetNumbers[i] || ""}</td>
                    {Object.keys(row).map((key) => (
                      <td key={key}>
                        <input
                          type="text"
                          value={row[key]}
                          onChange={(e) => handleEdit(i, key, e.target.value)}
                          style={{ width: "120px" }}
                        />
                      </td>
                    ))}
                    <td>{statusMap[i] || "⏳ Pending"}</td>
                    <td>
                      <button onClick={() => saveRow(row, i)} disabled={statusMap[i] === "✅ Saved"}>
                        Save
                      </button>
                      <button
                        onClick={() => deleteRow(i)}
                        style={{ marginLeft: 6, color: "red" }}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

export default UploadForm;
