import React, { useEffect, useState } from "react";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { collection, addDoc } from "firebase/firestore";
import { auth, db } from "./firebase";
import Auth from "./components/Auth";
import UploadForm from "./components/UploadForm";
import ManualEntryForm from "./components/ManualEntryForm";

function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  const handleSave = async () => {
    try {
      const batch = records.map(record => addDoc(collection(db, "fleet_records"), {
        ...record,
        createdBy: user.email,
        createdAt: new Date()
      }));
      await Promise.all(batch);
      alert("Records saved to Firestore!");
    } catch (error) {
      alert("Error saving records: " + error.message);
    }
  };

  if (!user) return <Auth />;

  return (
    <div style={{ padding: 20 }}>
      <h2>Welcome, {user.email}</h2>
      <button onClick={() => signOut(auth)}>Logout</button>
      <hr />
      <UploadForm onDataParsed={(data) => setRecords([...records, ...data])} />
      <ManualEntryForm onAddRow={(row) => setRecords([...records, row])} />
      <hr />
      <h4>Editable Data Table</h4>
      <table border="1">
        <thead>
          <tr>
            {records[0] && Object.keys(records[0]).map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {records.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {Object.keys(row).map((col, colIndex) => (
                <td key={colIndex}>
                  <input
                    type="text"
                    value={records[rowIndex][col]}
                    onChange={(e) => {
                      const updated = [...records];
                      updated[rowIndex][col] = e.target.value;
                      setRecords(updated);
                    }}
                    style={{ width: "100px" }}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <button style={{ marginTop: 20 }} onClick={handleSave}>Save to Firestore</button>
    </div>
  );
}

export default App;
