// UploadForm.js
import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import { db, auth } from "../firebase";
import {
  collection,
  addDoc,
  doc,
  runTransaction,
  Timestamp,
} from "firebase/firestore";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// 1. Final field structure (flattened)
const finalFields = [
  // Fixed
  "date", "deliveryDate", "months", "origin", "destination", "customer", "customerType", "vehicleNo", "vendor", "salesRate", "buyRate",
  // Customer
  "toBeAdvance", "advanceReceived", "advDeviation", "advanceRecDate", "validatedAdvanceUTRDescription", "validatedAdvanceAmount", "balance", "processingCharges", "inwardMisCharges", "outwardMisCharges", "balanceReceived", "remainingBalance", "balanceRecDate", "validatedBalanceUTR", "validatedBalanceUTRAmount",
  // Vendor
  "vendorOutwardPayment", "paidAmount", "balancePending", "vendorRemark",
  // POD
  "podVendorDate", "podSendToCustomerDate", "docNo", "podCustomerRec", "today", "balanceOverdueDays", "toBeCollectedAmount"
];

const keyToLabel = {
  "date": "Placement Date",
  "deliveryDate": "Delivery Date",
  "months": "Months",
  "origin": "Origin",
  "destination": "Destination",
  "customer": "Customer",
  "customerType": "Customer Type",
  "vehicleNo": "Vehicle No",
  "vendor": "Vendor",
  "salesRate": "Sales Rate",
  "buyRate": "Buy Rate",
  "toBeAdvance": "To be Advance (Sales)",
  "advanceReceived": "Advance/Payment Received",
  "advDeviation": "Adv Deviation (as on Date)",
  "advanceRecDate": "Advance Rec Date",
  "validatedAdvanceUTRDescription": "Validated-Advance UTR Description",
  "validatedAdvanceAmount": "Validated UTR -Advance Amount",
  "balance": "Balance",
  "processingCharges": "Processing Charges",
  "inwardMisCharges": "Inward-Mis Charges",
  "outwardMisCharges": "Outward-Mis Charges",
  "balanceReceived": "Bal Received",
  "remainingBalance": "Remaining Balance",
  "balanceRecDate": "Balance Rec Date",
  "validatedBalanceUTR": "Validated-Balance UTR",
  "validatedBalanceUTRAmount": "Validate Balance UTR-Amount",
  "vendorOutwardPayment": "Vendor Outward Payment",
  "paidAmount": "Paid Amount",
  "balancePending": "Balance Pending",
  "vendorRemark": "Vendor Remark",
  "podVendorDate": "POD Rev-Vendor-Date",
  "podSendToCustomerDate": "POD-Send to Customer Date",
  "docNo": "Doc No",
  "podCustomerRec": "POD-Customer Rec",
  "today": "Today",
  "balanceOverdueDays": "Balance Overdue Days",
  "toBeCollectedAmount": "To be Collected Amount"
};

const labelToKey = Object.fromEntries(
  Object.entries(keyToLabel).map(([k, v]) => [v, k])
);

const dateFields = [
  "date", "deliveryDate",
  "advanceRecDate", "balanceRecDate",
  "podVendorDate", "podSendToCustomerDate", "podCustomerRec", "today"
];

const cleanDate = (value) => {
  const parsed = new Date(value);
  return !isNaN(parsed) ? parsed.toISOString().split("T")[0] : "";
};

const UploadForm = () => {
  const [rows, setRows] = useState([]);
  const [fileName, setFileName] = useState("");

  useEffect(() => {
    const saved = localStorage.getItem("uploadPreviewRows");
    if (saved) {
      setRows(JSON.parse(saved));
    }
  }, []);

  const handleFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.name.endsWith(".xls") && !file.name.endsWith(".xlsx")) {
      toast.error("‚ùå Please upload a valid Excel file (.xls or .xlsx)");
      return;
    }

    setFileName(file.name);
    const reader = new FileReader();

    reader.onload = (event) => {
      const workbook = XLSX.read(event.target.result, { type: "binary" });
      const sheet = workbook.Sheets[workbook.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(sheet, { defval: "" });

      const processed = jsonData.map((row) => {
        const result = {};
        Object.entries(row).forEach(([label, value]) => {
          const key = labelToKey[label];
          if (key) {
            result[key] = dateFields.includes(key) ? cleanDate(value) : value;
          }
        });
        return result;
      });

      setRows(processed);
      localStorage.setItem("uploadPreviewRows", JSON.stringify(processed));
    };

    reader.readAsBinaryString(file);
  };

  const handleSubmitAll = async () => {
    if (!rows.length) {
      toast.warn("‚ö†Ô∏è No data to submit");
      return;
    }

    try {
      const batch = [];
      for (const row of rows) {
        const indentNo = await getNextIndentNumber();
        const enriched = {
          indentNumber: indentNo,
          ...convertDateFields(row),
          createdAt: new Date(),
          createdBy: auth.currentUser?.email || "anonymous",
          isCurrent: true,
          versionDate: new Date(),
          updateDescription: "Excel Upload"
        };
        await addDoc(collection(db, "fleet_records"), enriched);
        batch.push(indentNo);
      }

      toast.success(`‚úÖ Uploaded ${batch.length} records`);
      localStorage.removeItem("uploadPreviewRows");
      setRows([]);
    } catch (err) {
      console.error(err);
      toast.error("‚ùå Upload failed: " + err.message);
    }
  };

  const convertDateFields = (data) => {
    const result = {};
    Object.entries(data).forEach(([key, val]) => {
      if (dateFields.includes(key)) {
        const parsed = new Date(val);
        result[key] = isNaN(parsed) ? val : Timestamp.fromDate(parsed);
      } else {
        result[key] = val;
      }
    });
    return result;
  };

  const getNextIndentNumber = async () => {
    const counterRef = doc(db, "Counters", "fleet_counter");
    const newIndentNo = await runTransaction(db, async (transaction) => {
      const docSnap = await transaction.get(counterRef);
      if (!docSnap.exists()) throw new Error("Counter doc missing");
      const current = docSnap.data().nextFleetNo || 1;
      transaction.update(counterRef, { nextFleetNo: current + 1 });
      return current;
    });
    return newIndentNo;
  };

  const downloadTemplate = () => {
    const headers = Object.values(keyToLabel); // Human labels
    const fieldKeys = Object.keys(keyToLabel); // Firestore keys

    const sampleRow = {};
    fieldKeys.forEach((key) => {
      sampleRow[key] = dateFields.includes(key) ? "2025-07-01" : "";
    });

    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.json_to_sheet([sampleRow], {
      header: fieldKeys,
      skipHeader: true,
      origin: "A2"
    });

    headers.forEach((label, index) => {
      const cell = XLSX.utils.encode_cell({ c: index, r: 0 });
      ws[cell] = { v: label };
    });

    XLSX.utils.book_append_sheet(wb, ws, "FleetUploadTemplate");
    XLSX.writeFile(wb, "FleetUploadTemplate.xlsx");
  };

  return (
    <div className="container mt-4">
      <h4>üì§ Upload Excel File</h4>
      <div style={{ display: "flex", alignItems: "center", gap: "15px", marginTop: "10px" }}>
        <input type="file" accept=".xlsx, .xls" onChange={handleFile} />
        <button className="btn btn-secondary" onClick={downloadTemplate}>
          ‚¨áÔ∏è Download Template
        </button>
      </div>

      <div style={{ marginTop: "20px" }}>
        {rows.length > 0 && (
          <>
            <h5>üìÑ Preview & Edit Data</h5>
            <div className="table-scroll-x">
              <table>
                <thead>
                  <tr>
                    {Object.keys(rows[0]).map((col) => (
                      <th key={col}>{keyToLabel[col] || col}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {rows.map((row, rowIndex) => (
                    <tr key={rowIndex}>
                      {Object.keys(row).map((col) => (
                        <td key={col}>
                          <input
                            type={dateFields.includes(col) ? "date" : "text"}
                            value={row[col]}
                            onChange={(e) => {
                              const updated = [...rows];
                              updated[rowIndex][col] = e.target.value;
                              setRows(updated);
                              localStorage.setItem("uploadPreviewRows", JSON.stringify(updated));
                            }}
                          />
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div style={{ textAlign: "right", marginTop: "20px" }}>
              <button onClick={handleSubmitAll} className="btn btn-success me-3">
                ‚úÖ Submit All Records
              </button>
              <button
                onClick={() => {
                  setRows([]);
                  localStorage.removeItem("uploadPreviewRows");
                  toast.info("üßπ Cleared uploaded preview data");
                }}
                className="btn btn-warning"
              >
                üîÑ Upload New Sheet
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default UploadForm;
