import React, { useEffect, useState } from "react";
import { onAuthStateChanged, signOut } from "firebase/auth";
import {
  collection,
  query,
  where,
  getDocs,
  getDoc,
  updateDoc,
  deleteDoc,
  doc,
  addDoc,
} from "firebase/firestore";
import { auth, db } from "./firebase";
import Auth from "./components/Auth";
import ManualEntryForm from "./components/ManualEntryForm";
import UploadForm from "./components/UploadForm";
import "./App.css";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);
  const [history, setHistory] = useState([]);
  const [searchKey, setSearchKey] = useState("");
  const [searchField, setSearchField] = useState("fleetNumber");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [originalRecords, setOriginalRecords] = useState({});
  const [isSearching, setIsSearching] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [searchAttempted, setSearchAttempted] = useState(false);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);


  const handleSearch = async () => {
    setIsSearching(true);
    try {
      const fleetRef = collection(db, "fleet_records");
      let q;

      if (searchField === "Date") {
        if (!startDate || !endDate) {
          toast.warn("üìÖ Please select both From and To dates.");
          setIsSearching(false);
          return;
        }

        const fromDate = new Date(startDate);
        const toDate = new Date(endDate + "T23:59:59");

        if (fromDate > toDate) {
          toast.warn("‚ö†Ô∏è 'From' date cannot be after 'To' date.");
          setIsSearching(false);
          return;
        }

        q = query(fleetRef, where("createdAt", ">=", fromDate), where("createdAt", "<=", toDate));
      } else if (searchKey) {
        const key = searchField === "fleetNumber" ? Number(searchKey) : searchKey;
        q = query(fleetRef, where(searchField, "==", key));
      } else {
        toast.warn("üîç Please enter a search value.");
        setIsSearching(false);
        return;
      }

      setSearchAttempted(true);
      const snapshot = await getDocs(q);
      const allVersions = [];
      const currentOnly = [];

      snapshot.forEach((docSnap) => {
        const row = { id: docSnap.id, ...docSnap.data() };
        allVersions.push(row);
        if (row.isCurrent) currentOnly.push(row);
      });

      if (searchField === "fleetNumber") {
        setRecords(currentOnly);
        setHistory(allVersions);
      } else {
        setRecords([]);
        setHistory(allVersions);
      }

      if (snapshot.empty || allVersions.length === 0) {
        toast.info("‚ùå No records found.");
      }
    } catch (error) {
      toast.error("‚ùå Search failed: " + error.message);
    } finally {
      setIsSearching(false);
    }
  };

    const normalize = (val) => {
    if (val === null || val === undefined) return "";
    return typeof val === "object" && val.seconds
      ? new Date(val.seconds * 1000).toISOString()
      : String(val).trim();
  };

  const getChangedFields = (current, original) => {
    const ignored = ["versionDate", "expiredAt", "isCurrent", "updateDescription", "createdAt", "createdBy"];
    const changed = [];
    for (const key in current) {
      if (ignored.includes(key)) continue;
      const currVal = normalize(current[key]);
      const origVal = normalize(original?.[key]);
      if (currVal !== origVal) changed.push(key);
    }
    return changed;
  };

  const hasMeaningfulChanges = (curr, orig) => getChangedFields(curr, orig).length > 0;


    const handleUpdate = async (row) => {
    try {
      const { id, fleetNumber, ...updatedData } = row;

      const currentDocSnap = await getDocs(
        query(collection(db, "fleet_records"), where("fleetNumber", "==", fleetNumber), where("isCurrent", "==", true))
      );
      const oldDoc = currentDocSnap.docs.find(doc => doc.id === id);
      const oldData = oldDoc?.data() || {};

      const cleanData = {};
      const updateLog = [];
      for (const key in updatedData) {
        if (["id", "isCurrent", "createdAt", "createdBy", "versionDate", "updateDescription", "expiredAt"].includes(key))
          continue;
        const newVal = updatedData[key] ?? "";
        const oldVal = oldData[key] ?? "";
        cleanData[key] = newVal;

        if (String(newVal).trim() !== String(oldVal).trim()) {
          updateLog.push(`Updated ${key}`);
        }
      }

      if (updateLog.length === 0) {
        toast.info("‚ÑπÔ∏è No changes to update.");
        return;
      }

      const confirm = window.confirm(`Update changes: ${updateLog.join(", ")}?`);
      if (!confirm) return;

      await updateDoc(doc(db, "fleet_records", id), {
        isCurrent: false,
        expiredAt: new Date(),
        modifiedBy: user.email
      });

      const newVersion = {
        ...cleanData,
        fleetNumber,
        createdAt: new Date(),
        createdBy: user.email,
        isCurrent: true,
        versionDate: new Date(),
        updateDescription: updateLog.join(", ")
      };

      await addDoc(collection(db, "fleet_records"), newVersion);

      toast.success("‚úÖ Record updated.");
      handleSearch();
    } catch (error) {
      toast.error("‚ùå Update failed: " + error.message);
    }
  };

  const handleDelete = async (fleetNumberToDelete) => {
    try {
      const q = query(collection(db, "fleet_records"), where("fleetNumber", "==", fleetNumberToDelete));
      const snapshot = await getDocs(q);
      if (snapshot.empty) return alert("‚ùå No records found.");

      const confirmDelete = window.confirm(`Delete all versions of fleet ${fleetNumberToDelete}?`);
      if (!confirmDelete) return;

      await Promise.all(snapshot.docs.map(docSnap => deleteDoc(doc(db, "fleet_records", docSnap.id))));
      setRecords(prev => prev.filter(r => r.fleetNumber !== fleetNumberToDelete));
      setHistory(prev => prev.filter(r => r.fleetNumber !== fleetNumberToDelete));
      toast.success("‚úÖ Deleted all versions.");
    } catch (err) {
      toast.error("‚ùå Delete failed: " + err.message);
    }
  };
  if (!user) return <Auth />;

  return (
    <div style={{ maxWidth: "100vw", overflowX: "hidden", padding: "40px 20px" }}>
      <div className="header">
        <h2>Fleet Billing Dashboard</h2>
        <div style={{ display: "flex", alignItems: "center" }}>
          <span className="user-email">Welcome, {user.email}</span>
          <img src="/logo.png" alt="Logo" style={{ height: "50px" }} />
        </div>
      </div>

      <button onClick={() => signOut(auth)}>Logout</button>
      <hr />

      <UploadForm />

      <ManualEntryForm
        onAddRow={(row, addToHistory) => {
          setRecords([row]);
          if (addToHistory) setHistory([row]);
        }}
      />

      <hr />
      <h4>Search Existing Records</h4>
      <div className="search-bar">
        <select value={searchField} onChange={(e) => {
          setSearchField(e.target.value);
          setSearchKey("");
          setStartDate("");
          setEndDate("");
        }}>
          <option value="fleetNumber">Fleet Number</option>
          <option value="Broker">Broker</option>
          <option value="Date">Date</option>
        </select>

        {searchField === "Date" ? (
          <>
            <label style={{ marginLeft: 10 }}>From:</label>
            <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
            <label style={{ marginLeft: 10 }}>To:</label>
            <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
          </>
        ) : (
          <input
            type="text"
            value={searchKey}
            onChange={(e) => setSearchKey(e.target.value)}
            placeholder="Search key"
            style={{ margin: "0 10px" }}
          />
        )}

        <button onClick={handleSearch} style={{ marginLeft: 10 }} disabled={isSearching}>
          {isSearching ? "Searching..." : "Search"}
        </button>
      </div>

      <hr />

      {searchField === "fleetNumber" && records.length > 0 && (
        <>
          <h4>Editable Current Records</h4>
          <div className="table-scroll-x">
            <table>
              <thead>
                <tr>
                  {Object.keys(records[0]).map((col) => (
                    <th key={col}>{col}</th>
                  ))}
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {records.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {Object.keys(row).map((col) => (
                      <td key={col}>
                        <input
                          type="text"
                          value={
                            typeof row[col] === "object" && row[col]?.seconds
                              ? new Date(row[col].seconds * 1000).toLocaleString()
                              : String(row[col] ?? "")
                          }
                          onChange={(e) => {
                            const updated = [...records];
                            updated[rowIndex][col] = e.target.value;
                            setRecords(updated);
                          }}
                          readOnly={["fleetNumber", "createdAt", "createdBy", "isCurrent", "updateDescription"].includes(col)}
                          style={{ width: "140px" }}
                        />
                      </td>
                    ))}
                    <td>
                      <button
                        className="save"
                        disabled={!hasMeaningfulChanges(row, originalRecords[row.id])}
                        onClick={() => handleUpdate(row)}
                      >
                        Save
                      </button>
                      <button className="delete" onClick={() => handleDelete(row.fleetNumber)}>
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}

      {history.length > 0 && (
        <>
          <hr />
          <h4>üîç Full Version History</h4>
          <div className="table-scroll-x">
            <table>
              <thead>
                <tr>
                  {[
                    "fleetNumber",
                    ...[...new Set(history.flatMap((r) => Object.keys(r)))].filter((k) => k !== "fleetNumber").sort(),
                  ].map((col) => (
                    <th key={col}>{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {[...history]
                  .sort((a, b) => {
                    if (a.isCurrent) return -1;
                    if (b.isCurrent) return 1;
                    const aTime = a.expiredAt?.seconds || 0;
                    const bTime = b.expiredAt?.seconds || 0;
                    return bTime - aTime;
                  })
                  .map((row, i) => (
                    <tr key={i}>
                      {[
                        "fleetNumber",
                        ...[...new Set(history.flatMap((r) => Object.keys(r)))].filter((k) => k !== "fleetNumber").sort(),
                      ].map((col, j) => (
                        <td key={j}>
                          {typeof row[col] === "object" && row[col]?.seconds
                            ? new Date(row[col].seconds * 1000).toLocaleString()
                            : String(row[col] ?? "")}
                        </td>
                      ))}
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </>
      )}

      {searchAttempted && !isSearching && records.length === 0 && history.length === 0 && (
        <p style={{ textAlign: "center", color: "#888", marginTop: "30px" }}>No records to display.</p>
      )}

      <ToastContainer position="bottom-right" autoClose={3000} />
      <p style={{ textAlign: "center", marginTop: "30px", color: "#666" }}>¬© 2025 Fleet Billing System</p>
    </div>
  );
}

export default App;
